import FoodList from "./FoodList.js";
import { useEffect, useState } from "react";
import { getFoods } from "../api.js";

const LIMIT = 2;

function App() {
  const [items, setItems] = useState([]);
  const [order, setOrder] = useState("createdAt");
  const [offset, setOffset] = useState(0);
  const sortedItems = items.sort((a, b) => b[order] - a[order]);

  const handleNewestClick = () => setOrder("createdAt");
  const handleCalorieClick = () => setOrder("calorie");
  const handleDelete = (id) => {
    const nextItems = items.filter((item) => item.id !== id);
    setItems(nextItems);
  };

  const handleLoad = async (options) => {
    const { foods } = await getFoods(options);
    if (options.offset === 0) {
      setItems(foods);
    } else {
      setItems([...items, ...foods]);
    }
    setOffset(options.offset + foods.length);
  };

  const handleLoadMore = () => {
    handleLoad({ order, offset, limit: LIMIT });
  };

  useEffect(() => {
    handleLoad({ order, offset: 0, limit: LIMIT });
  }, [order]);
  return (
    <div>
      <div>
        <button onClick={handleNewestClick}>Newest</button>
        <button onClick={handleCalorieClick}>calorie</button>
      </div>
      <FoodList items={sortedItems} onDelete={handleDelete} />
    </div>
  );
}

export default App;
